import {
  Component,
  Input,
  OnInit,
  ElementRef,
  ViewChild,
  Renderer2 
} from '@angular/core';

@Component({
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.sass']
})
export class PaginationComponent implements OnInit{
  @ViewChild('btnPrev') btnPrev!: ElementRef;
  @ViewChild('btnNext') btnNext!: ElementRef;
  @Input() public items!: number;
  public pages: any;
  public current_page = 1;

  constructor(private renderer: Renderer2) {}

  ngOnInit (): void {
    this.renderer.setStyle(this.btnPrev, "visibility", "hidden");
    console.log(this.items)
    if (this.items){
      const pagesNumber = this.getNumPages()
      console.log(pagesNumber)
      this.pages = Array.from({ length: pagesNumber }, (_, index) => index + 1);
      console.log(this.pages)
    }
  }
  getNumPages () {
    return Math.ceil(this.items / 100);
  }  
  prevPage () {
    if (this.current_page > 1) {
      this.current_page--;
      this.change(this.current_page);
    }
  }

  nextPage () {
    if (this.items){ 
      this.pages = this.items / 20 // Cards on display limit
      if (this.current_page < this.pages) {
        this.current_page++;
      }
      this.change(this.current_page);
    }
  }

  change(page: number) {
    if (page < 1) page = 1;
    if (page > this.getNumPages()) page = this.getNumPages();
    if (page == 1) {
        this.renderer.setStyle(this.btnPrev, "visibility", "hidden");
    } else {
      this.renderer.setStyle(this.btnPrev, "visibility", "visible");
    }
    if (page == this.getNumPages()) {
      this.renderer.setStyle(this.btnNext, "visibility", "hidden");
    } else {
      this.renderer.setStyle(this.btnNext, "visibility", "visible");
    }
  }
}
<div class="pagination">
  Pages:
  <button class="arrow" (click)="prevPage()"> < </button>
  <button *ngFor="let page of pages; index as i">
    {{ page }}
  </button>
  <button class="arrow" (click)="nextPage()"> > </button>
</div>
