import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.sass']
})
export class CharacterDetailsComponent implements OnInit{
  @Input() public data!: any;

  ngOnInit (): void {
    if (this.data){
      console.log(this.data)
    }
  }
}
<div class="character-details">
  {{data.name}}

  <span *ngIf="data.description"> {{data.description}} </span>
  <span *ngElse> Description not available </span>
  <img [src]="char.thumbnail.path + '.' + char.thumbnail.extension" alt="character">
  <div class="stories" *ngFor="let story of stories">
    {{ story.name }}
  </div>

</div>
