import { Component, OnInit } from '@angular/core';
import { CharactersModel } from 'src/app/models/characters.model';
import { MarvelService } from 'src/app/services/marvel.service';

@Component({
  selector: 'app-characters',
  templateUrl: './characters.component.html',
  styleUrls: ['./characters.component.sass']
})
export class CharactersComponent implements OnInit{
  characters: CharactersModel[] = [];

  constructor(private marvelService: MarvelService) {}

  ngOnInit() {
    this.marvelService.getData('characters').subscribe((characters) => {
      this.characters = characters.data.results;
      console.log(this.characters)
    });
  }
}
<app-navbar></app-navbar>
<div class="characters-page">
  <div class="characters-page-item"
    *ngFor="let char of characters"
  >
    <span> {{ char.name }} </span>
    <img [src]="char.thumbnail.path + '.' + char.thumbnail.extension" alt="character">
    <span> Number of stories: {{ char.stories.available }} </span>
  </div>

</div>
