import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../environments/environment';
import {Md5} from 'ts-md5';

@Injectable({
  providedIn: 'root'
})
export class MarvelService {
  private apiUrl = 'https://gateway.marvel.com/v1/public/';
  private apiKey = environment.marvelPubKey

  constructor(private http: HttpClient) { }

  getComics()  {
    const timestamp = Date.now().toString();
    const hash = 'TU_HASH'; // Puedes calcular un hash utilizando tu clave secreta y el timestamp.

    const url = `${this.apiUrl}comics?apikey=${this.apiKey}&hash=${hash}`;
    return this.http.get(url);
  }
}
