import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.sass']
})
export class CharacterDetailsComponent {
  @Input() public data!: any;
}
<div class="character-details">
  {{data.name}}

  <span *ngIf="data.description.length > 1"> {{data.description}} </span>
  <span *ngIfElse> Description not available </span>
  <img [src]="data.thumbnail.path + '.' + data.thumbnail.extension" alt="character">
  <div class="stories" *ngFor="let story of data.stories.items">
    {{ story.name }}
  </div>

</div>
